xwikidoc.content:
{{groovy}}
import com.xpn.xwiki.store.*

if (request.getParameter("confirm") == "1") {
  def hibStore = services.component.getInstance(XWikiStoreInterface.class, "hibernate")
  def ctx = xcontext.context

  hibStore.checkHibernate(ctx)
  def bTransaction = hibStore.beginTransaction(false, ctx)
  def session = hibStore.getSession(ctx)

  def statement = session.connection().createStatement();

  statement.executeQuery("truncate table glimpse_services_properties;");
  statement.executeQuery("truncate table glimpse_services;");
  statement.executeQuery("truncate table glimpse_agents");

  hibStore.endTransaction(ctx, true)
} else {
  println("[[Flush Agents Data (Agents pages won't be modified)>>${doc.getFullName()}||queryString='confirm=1']]")
}
{{/groovy}}
